mapper.insert:

INSERT INTO emp(emp_num, emp_nm, zipcode, addr1, addr2, phone_num, salary) 
VALUES 
(
	(SELECT NVL(MAX(emp_num), TO_CHAR(SYSDATE, 'yyyyMM') || '000') + 1 
	 FROM emp
	 WHERE SUBSTR(emp_num, 0, 6) = TO_CHAR(SYSDATE, 'yyyyMM'))
	,#{empNm}
	,#{zipcode}
	,#{addr1}
	,#{addr2}
	,#{phoneNum}
	,#{salary}
)
